current.custom_css:
html[dir="ltr"] .header-bottom .main-menu &gt; li:first-child &gt; a:before {
  left: -1.5rem;
}
html[dir="rtl"] .header-bottom .main-menu &gt; li:first-child &gt; a:before {
  right: -1.5rem;
}
.section-title h3 {
  letter-spacing: .1em;
}
.md_products_section .box-tabs a {
  color: #333;
  border-width: 0 0 1px 0;
  border-style: solid;
}
.md_products_section .box-tabs a:not(.active) {
  border-color: transparent;
}
.icon-box-side .icon-box-icon {
  font-size: 3.4rem;
}
.brands-slide.style-default .icon__brand {
    border-color: #444;
}
.imgs__banner_template7 .item__2 .text-content .btn {
    color: white;
    border-color: white;
}

settings.css_section:
.section_id .slideshow__subtitle sup {
  vertical-align: top;
  font-size: 1.3rem;
  top: 1em;
  margin-right: 0;
  letter-spacing: -.01em;
}
@media screen and (min-width: 768px){
  .section_id .slideshow__subtitle sup {
    font-size: 1.6rem;
  }
}
@media screen and (min-width: 992px){
  .section_id .slideshow__subtitle sup {
    font-size: 1.8rem;
  }
}
@media screen and (min-width: 1200px){
  .section_id .slideshow__subtitle sup {
    font-size: 2rem;
  }
}
